      .scribbleInMobile {
        display: block;
        stroke: white;
        position: fixed;
        top: 0;
        left: 0;
        width: 150vw;
        height: 100%; 
        overflow: clip;
        z-index: 100;
        pointer-events: none;
        stroke-dasharray: 0;
        stroke-dashoffset: 0;
        animation: animation_scribbleIn 2s linear forwards;
        .scribbleInSVG{
              width: 100%;
              height: 100%;
              /* Preserve aspect ratio and cover entire container */
              object-fit: cover;
        }
      }
    }
  }
}

/*----------------------------------- Desktop variant van scribble animatie --------------------------------------*/

@media (min-width: 1240px){

.scribbleInMobile{
