        justify-content: space-around;
        gap: 2rem;
    }

    p{
        font-size: clamp(60px, 15vw, 90px);
        position: relative;
        z-index: 2;
        margin: 0;
    }
}

.bekijkDeKlassenHeading{
    margin-top: 13vh;
    width: 99%;
    height: auto;
    max-width: 800px;
    @media (min-width: 700px){
         margin-top: 11vh;
    }
}

.bekijkDeKlassenHeadingHidden{
    height: 1px;
    overflow: hidden;
    position: absolute;
    white-space: nowrap;
    width: 1px;
    z-index: 1;
}

.classNavigation {
    position: relative;
    width: 50%;
    height: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    
    @media (min-width: 768px) {
        height: 250px;
    }
    
    @media (min-width: 1100px) {
        height: 300px;
    }
}

.scribbleBackground {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    
    @media (prefers-reduced-motion: no-preference) {
        path {
            transition: stroke-dashoffset 1.2s ease-in-out;
        }
    }
}

/* Hover animation */
.classNavigation:hover .scribbleBackground path {
    stroke-dashoffset: 0;
}

/* Optional: Add some scaling effect on hover */
.classNavigation:hover {
    transform: scale(1.05);
    transition: transform 0.3s ease;
}

</style>